import React, {useState} from 'react';
import "./Navigation.scss";
import ListIcon from '@material-ui/icons/List';
import Home from "../../../asset/icons8-home.svg";
import {GoHome} from "react-icons/go";
import {
    Link
} from "react-router-dom";

let linkHome = <Link to="/home" >Home</Link>
let linkTask = <Link to="/Task" >My Task</Link>
const constantMenu: any = [
    {
        icon:  <Link to="/home" ><GoHome /></Link>,
        name: linkHome
    },
    {
        icon: <Link to="/task" ><GoHome /></Link>,
        name: linkTask
    },
    {
        icon: <GoHome />,
        name: "Inbox"
    },
    {
        icon: <GoHome />,
        name: "Reporting"
    },
    {
        icon: <GoHome />,
        name: "Portfolios"
    },
    {
        icon: <GoHome />,
        name: "Goals"
    }
]

const Navigation = () => {
    let clickBorder = (index: number) => {
        console.log("index", index);
        let navigation = document.getElementsByClassName("item-body__navigation") as HTMLCollectionOf<any>;
        console.log("navigation", navigation)
        if (index === 0) {
            linkHome = <Link to="/home" >Home</Link>;
            navigation[0].style.background = "#FFFFFF";
            navigation[0].style.width = "100%";
            navigation[0].style.height = "40px";
            navigation[0].style.cursor = "pointer";
            navigation[1].style.background = "";
            navigation[1].style.width = "";
            navigation[1].style.height = "";
            navigation[1].style.cursor = "";
            navigation[1].style.borderLeft = "";
            navigation[2].style.background = "";
            navigation[2].style.width = "";
            navigation[2].style.height = "";
            navigation[2].style.cursor = "";
            navigation[2].style.borderLeft = "";
            navigation[3].style.background = "";
            navigation[3].style.width = "";
            navigation[3].style.height = "";
            navigation[3].style.cursor = "";
            navigation[3].style.borderLeft = "";
            navigation[4].style.background = "";
            navigation[4].style.width = "";
            navigation[4].style.height = "";
            navigation[4].style.cursor = "";
            navigation[4].style.borderLeft = "";
            navigation[5].style.background = "";
            navigation[5].style.width = "";
            navigation[5].style.height = "";
            navigation[5].style.cursor = "";
            navigation[5].style.borderLeft = "";
        }
        if (index === 1) {
            navigation[0].style.background = "";
            navigation[0].style.width = "";
            navigation[0].style.height = "";
            navigation[0].style.cursor = "";
            navigation[0].style.borderLeft = "";
            navigation[1].style.background = "#FFFFFF";
            navigation[1].style.width = "100%";
            navigation[1].style.height = "40px";
            navigation[1].style.cursor = "pointer";
            navigation[2].style.background = "";
            navigation[2].style.width = "";
            navigation[2].style.height = "";
            navigation[2].style.cursor = "";
            navigation[2].style.borderLeft = "";
            navigation[3].style.background = "";
            navigation[3].style.width = "";
            navigation[3].style.height = "";
            navigation[3].style.cursor = "";
            navigation[3].style.borderLeft = "";
            navigation[4].style.background = "";
            navigation[4].style.width = "";
            navigation[4].style.height = "";
            navigation[4].style.cursor = "";
            navigation[4].style.borderLeft = "";
            navigation[5].style.background = "";
            navigation[5].style.width = "";
            navigation[5].style.height = "";
            navigation[5].style.cursor = "";
            navigation[5].style.borderLeft = "";
        }
        if (index === 2) {
            navigation[0].style.background = "";
            navigation[0].style.width = "";
            navigation[0].style.height = "";
            navigation[0].style.cursor = "";
            navigation[1].style.background = "";
            navigation[1].style.width = "";
            navigation[1].style.height = "";
            navigation[1].style.cursor = "";
            navigation[2].style.background = "#FFFFFF";
            navigation[2].style.width = "100%";
            navigation[2].style.height = "40px";
            navigation[2].style.cursor = "pointer";
            navigation[3].style.background = "";
            navigation[3].style.width = "";
            navigation[3].style.height = "";
            navigation[3].style.cursor = "";
            navigation[4].style.background = "";
            navigation[4].style.width = "";
            navigation[4].style.height = "";
            navigation[4].style.cursor = "";
            navigation[5].style.background = "";
            navigation[5].style.width = "";
            navigation[5].style.height = "";
            navigation[5].style.cursor = "";
        }
        if (index === 3) {
            navigation[0].style.background = "";
            navigation[0].style.width = "";
            navigation[0].style.height = "";
            navigation[0].style.cursor = "";
            navigation[1].style.background = "";
            navigation[1].style.width = "";
            navigation[1].style.height = "";
            navigation[1].style.cursor = "";
            navigation[2].style.background = "";
            navigation[2].style.width = "";
            navigation[2].style.height = "";
            navigation[2].style.cursor = "";
            navigation[3].style.background = "#FFFFFF";
            navigation[3].style.width = "100%";
            navigation[3].style.height = "40px";
            navigation[3].style.cursor = "pointer";
            navigation[4].style.background = "";
            navigation[4].style.width = "";
            navigation[4].style.height = "";
            navigation[4].style.cursor = "";
            navigation[5].style.background = "";
            navigation[5].style.width = "";
            navigation[5].style.height = "";
            navigation[5].style.cursor = "";
        }
        if (index === 4) {
            navigation[3].style.background = "";
            navigation[3].style.width = "";
            navigation[3].style.height = "";
            navigation[3].style.cursor = "";
            navigation[0].style.background = "";
            navigation[0].style.width = "";
            navigation[0].style.height = "";
            navigation[0].style.cursor = "";
            navigation[1].style.background = "";
            navigation[1].style.width = "";
            navigation[1].style.height = "";
            navigation[1].style.cursor = "";
            navigation[2].style.background = "";
            navigation[2].style.width = "";
            navigation[2].style.height = "";
            navigation[2].style.cursor = "";
            navigation[4].style.background = "#FFFFFF";
            navigation[4].style.width = "100%";
            navigation[4].style.height = "40px";
            navigation[4].style.cursor = "pointer";
            navigation[5].style.background = "";
            navigation[5].style.width = "";
            navigation[5].style.height = "";
            navigation[5].style.cursor = "";
        }
        if (index === 5) {
            navigation[3].style.background = "";
            navigation[3].style.width = "";
            navigation[3].style.height = "";
            navigation[3].style.cursor = "";
            navigation[0].style.background = "";
            navigation[0].style.width = "";
            navigation[0].style.height = "";
            navigation[0].style.cursor = "";
            navigation[1].style.background = "";
            navigation[1].style.width = "";
            navigation[1].style.height = "";
            navigation[1].style.cursor = "";
            navigation[2].style.background = "";
            navigation[2].style.width = "";
            navigation[2].style.height = "";
            navigation[2].style.cursor = "";
            navigation[4].style.background = "";
            navigation[4].style.width = "";
            navigation[4].style.height = "";
            navigation[4].style.cursor = "";
            navigation[5].style.background = "#FFFFFF";
            navigation[5].style.width = "100%";
            navigation[5].style.height = "40px";
            navigation[5].style.cursor = "pointer";
        }
    }
    return (
        <div className="containerNavigation">
            <div className="item-header">
                <div className="item-header__logo">
                    <h3>Trello</h3>
                </div>
                <div className="item-header__icon">
                    <div className="item-header__icon--Menu">
                        <ListIcon className="iconMenu"/>
                    </div>
                </div>
            </div>
            <div className="item-body">
                {
                    constantMenu.map((res: any, index: number) => {
                        console.log("index", index);
                        console.log("res", res);
                        return <div className="item-body__navigation" key={index} onClick={() => clickBorder(index)}>
                            <div className="item-body__navigation--MenuBar">
                                <div className="iconTopbar">{res.icon}</div>
                                <div className="NameTopbar"> {res.name}</div>
                            </div>
                        </div>
                    })
                }
            </div>
        </div>
    );
}

export default Navigation;
