import React, {FormEvent, useEffect, useState} from 'react';
import "./listboard.scss";
import {useDispatch, useSelector} from "react-redux";
import * as actions from "../../../store/action/index"
import {isNumber} from "util";
const Listboard = () => {
    const [title,setTitle] = useState();
    const [isClickTitle,setIsClickTitle] = useState(false as boolean);
    const [order,setOrder] = useState(0  as number);
    const [indexs,setIndexs] = useState(0 as number);
    let listTask = useSelector((state:any) => state.main.listTask);
    let dispatch =  useDispatch();
    const changeValueTitle = (event:any) => {
        event.preventDefault();
        setTitle(event.target.value)
    }
    const postTaskTitle = async (event:FormEvent<any >) =>{
        event.preventDefault();
        if(listTask.data)
        {
            listTask.data.length += 1;
            setOrder(listTask.data.length)
            let action = await actions.AddTask(title,order);
            await dispatch(action);
        }
        setIsClickTitle(true)
    }
    const hideValue = (index:number) => {
        if(indexs === index)
        {
            setIsClickTitle(false)
        }
    }
    const showValue = (index:number) => {
        if(indexs === index)
        {
            setIsClickTitle(true)
        }
    }
    useEffect(()=>{
        let action = actions.GetTask();
        dispatch(action);
    },[])
    return (
        <div className="containerListBoard">
            {
                listTask.data && listTask.data.map((res:any,index:number) => {
                  return <div  className="itemadd-task" key={index}>
                      {!isClickTitle &&  <span className="itemadd-task__span"  onClick={()=>showValue(index)}>{res.title}</span> }
                      {isClickTitle && <input key={index} value={res.title} onClick={()=> hideValue(index)}  />}
                    </div>
                })
            }
            <div className="itemadd-task">
                <form onSubmit={(event)=>{postTaskTitle(event)}}>
                    { isClickTitle &&  <input placeholder="add Task" name="title" onChange={(event)=>changeValueTitle(event)} />}
                    <button onClick={(event)=>{postTaskTitle(event)}}>ThÃªm</button>
                </form>
            </div>
        </div>
    );
}

export default Listboard;
