import {put, call} from "redux-saga/effects";
import axios from "axios";
import {enviroment} from "../../enviroment/enviroment";
import * as action from "../action/index";
export function* addTask(actions: any) {
    let { title } = actions;
    let body = {
        title: title
    };
    console.log("123",body);
    try{
        yield axios.post(enviroment.api + "/main/posttask", body);
        getTask()
    }
    catch (err){
        console.log("err",err)
    }
}
export function* getTask(){
   const response:any = axios.get(enviroment.api + "/main/gettask");
   console.log("12312",response.data.title)
   try {
        put(action.GetTaskList(response.data.title))
   }
   catch (e){
       console.log(e);
   }
}
