import {put, call} from "redux-saga/effects";
import axios from "axios";
import {enviroment} from "../../enviroment/enviroment";
import * as action from "../action/index";

export function* addTask(actions: any) {
    let {title} = actions;
    console.log("title", actions.title)
    let body = {
        title: title
    };
    console.log("123", body);
    yield axios.post(enviroment.api + "/main/posttask", body).then(res => {
        console.log(res)
    });
}

export function* getTask() {
    let response: any = yield axios.get(enviroment.api + "/main/gettask");
    try {
        console.log(response)
        // yield put(action.GetTaskList(response))
    } catch (e) {
        console.log(e);
    }
}
