import * as actionTypes from "../action/actiontype";
import { updateObject } from "../shared/utility";

interface tsInitialState {
    isLoad: boolean,
    tokenCompare: string,
    listTask: Array,
}

const initialState: tsInitialState = {
    isLoad: false,
    tokenCompare: '',
    listTask: []
}
const changeState = (action:any,state:any) =>{
    return updateObject(state,{tokenCompare: action.token});
}

const mainReducer = (state = initialState,action:any) => {
    switch (action.type)
    {
        case actionTypes:
            return changeState(action,state);
        default :
            return state;
    }
}


export default mainReducer;
