import React, {FormEvent, useEffect, useState} from 'react';
import "./listboard.scss";
import {useDispatch, useSelector} from "react-redux";
import * as actions from "../../../store/action/index"
const Listboard = () => {
    const [title,setTitle] = useState();
    let dispatch =  useDispatch();
    const changeValueTitle = (event:any) => {
        event.preventDefault();
        console.log("123123",event.target.value);
        setTitle(event.target.value)
    }
    const postTaskTitle = async (event:FormEvent<any>) =>{
        event.preventDefault();
        let action = actions.AddTask(title);
        console.log("action12312",action)
        await dispatch(action);
    }
    useEffect(()=>{
        let action = actions.GetTask();
        dispatch(action);
    },[])
    let listTask = useSelector((state:any) => state.main.listTask);
    return (
        <div className="containerListBoard">
            {
                listTask.data && listTask.data.map((res:any,index:number) => {
                  return  <div className="itemadd-task" key={index}>
                      {res.title}
                    </div>
                })
            }
            <form className="itemadd-task" onSubmit={(event)=>{postTaskTitle(event)}}>
                <button onSubmit={(event)=>{postTaskTitle(event)}}>ThÃªm</button>
                <input placeholder="add Task" name="title" onChange={(event)=>changeValueTitle(event)} />
            </form>
        </div>
    );
}

export default Listboard;
