import {put, call} from "redux-saga/effects";
import axios from "axios";
import {enviroment} from "../../enviroment/enviroment";

export function* addTask (actions:any) {
    let { title } = actions
    let body = {
        title: title
    }
    yield axios.post(enviroment.api + "/main/gettask",body).then(res => {
        console.log(res)
    }).catch((err)=>{
        console.log(err)
    })
}
