    return (
        <>
            {
                listTask && listTask.map((res: any, index: number) => {
                    console.log("id in app",res._id)
                    return <form id="itemget-task" key={index}>
                        {indexState === index ? <input key={index} defaultValue={res.title} onClick={() => hideValue(index)}/> :
